/**
 * MapComponent.js
 * 
 * React component that renders a heatmap for temperature data using Leaflet.
 * Displays `tnormal` on the heatmap and `tavg` in tooltips.
 */

import React, { useEffect, useRef } from 'react';
import L from 'leaflet'; // Import the Leaflet library
import 'leaflet/dist/leaflet.css'; // Import Leaflet's CSS
import 'leaflet.heat'; // Import Leaflet Heatmap plugin

/**
 * MapComponent
 * 
 * Renders a map with heatmap visualization for temperature data.
 * Each point includes a tooltip with details for average temperature.
 * 
 * @component
 * @param {Object[]} [temperatures=[]] - Array of objects with latitude, longitude, tavg, and tnormal values.
 * @returns {JSX.Element} A map with heatmap visualization.
 */
function MapComponent({ temperatures = [] }) {
    const mapRef = useRef(null); // Reference to the Leaflet map instance
    const heatLayerRef = useRef(null); // Reference to the heatmap layer

    useEffect(() => {
        // Initialize the map only once
        if (!mapRef.current) {
            mapRef.current = L.map('heatmap').setView([48, -124], 7); // Centered on NW US
            L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
                attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
            }).addTo(mapRef.current);
        }

        if (temperatures.length === 0) return; // Skip rendering if no temperature data

        // Prepare heatmap data ([latitude, longitude, intensity])
        const heatmapData = temperatures
            .filter((point) => point.latitude && point.longitude && point.tnormal !== null)
            .map((point) => [point.latitude, point.longitude, point.tnormal]);

        // Remove the existing heatmap layer, if any
        if (heatLayerRef.current) {
            mapRef.current.removeLayer(heatLayerRef.current);
        }

        // Add a new heatmap layer
        heatLayerRef.current = L.heatLayer(heatmapData, {
            radius: 10,
            blur: 0,
            maxZoom: 10,
            gradient: {
                0.0: '#800080', // Purple
                0.1: '#0000FF', // Blue
                0.2: '#00FFFF', // Cyan
                0.3: '#00FF00', // Lime
                0.4: '#008000', // Green
                0.5: '#FFFF00', // Yellow
                0.6: '#FFA500', // Orange
                0.7: '#FF0000', // Red
                0.8: '#8B0000', // Dark Red
                1.0: '#000000', // Black
            },
        }).addTo(mapRef.current);

        // Cleanup on component unmount
        return () => {
            if (mapRef.current) {
                mapRef.current.remove();
                mapRef.current = null;
            }
        };
    }, [temperatures]);

    return (
        <div id="heatmap" style={{ height: '500px', width: '100%' }}>
            {temperatures.length === 0 && (
                <p style={{ textAlign: 'center', marginTop: '20px' }}>
                    Please select a date to fetch and display the heatmap.
                </p>
            )}
        </div>
    );
}

export default MapComponent;
