/**
 * DateSelector.js
 * 
 * Component for selecting a date limited to the first day of each month.
 * Triggers data retrieval when the "Fetch Data" button is clicked.
 */

import React, { useState } from 'react';

/**
 * DateSelector Component
 * 
 * Allows users to select a date restricted to the first day of each month and fetch data when submitted.
 * 
 * @component
 * @param {function} fetchTemperatureData - Function to fetch temperature data for the selected date.
 * @returns {JSX.Element} A date selector input and fetch button.
 */
function DateSelector({ fetchTemperatureData }) {
    // Initialize with the first of the current month
    const defaultDate = new Date();
    defaultDate.setDate(1);
    const formattedDefaultDate = defaultDate.toISOString().substring(0, 10);
    const [selectedDate, setSelectedDate] = useState(formattedDefaultDate);

    /**
     * handleDateChange
     * 
     * Updates the selected date and ensures it remains on the first day of the month.
     * 
     * @param {object} event - The date change event.
     */
    const handleDateChange = (event) => {
        const date = new Date(event.target.value);
        if (!isNaN(date.getTime())) { // Only update if the date is valid
            date.setDate(1); // Restrict to the first day of the month
            setSelectedDate(date.toISOString().substring(0, 10));
        }
    };

    /**
     * handleFetchData
     * 
     * Triggers the fetch action for the selected date.
     */
    const handleFetchData = () => {
        if (selectedDate) {
            fetchTemperatureData(selectedDate);
        } else {
            alert("Please select a valid date.");
        }
    };

    return (
        <div>
            <label>Select Date (First of Each Month Only): </label>
            <input
                type="date"
                value={selectedDate}
                onChange={handleDateChange}
                max={formattedDefaultDate}
            />
            <button onClick={handleFetchData}>Fetch Data</button>
        </div>
    );
}

export default DateSelector;
