/**
 * MapComponent.js
 * 
 * React component that renders a cell-based heatmap using Leaflet.
 * Each cell is represented by a rectangle with a color corresponding to `tnormal`.
 */

import React from 'react';
import { MapContainer, TileLayer, Rectangle, Tooltip } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

/**
 * MapComponent
 * 
 * Renders a cell-based heatmap for temperature data.
 * Each cell is color-coded based on average temperature and displays its value on hover.
 * 
 * @component
 * @param {Object[]} [temperatureData=[]] - Array of objects with latitude, longitude, tavg, and tnormal.
 * @returns {JSX.Element} A map with a grid-based heatmap overlay.
 */
function MapComponent({ temperatureData = [] }) {
    const center = [48, -124]; // Example center for the Washington area

    // Define a detailed 10-color gradient for average temperature
    const getColor = (value) => {
        if (value < 0) return "#4B0082";       // Indigo
        if (value < 5) return "#0000FF";       // Blue
        if (value < 10) return "#00BFFF";      // Deep Sky Blue
        if (value < 15) return "#00FF7F";      // Spring Green
        if (value < 20) return "#7FFF00";      // Chartreuse
        if (value < 25) return "#FFFF00";      // Yellow
        if (value < 30) return "#FFA500";      // Orange
        if (value < 35) return "#FF4500";      // Orange Red
        if (value < 40) return "#FF0000";      // Red
        return "#8B0000";                      // Dark Red
    };

    // Helper function to calculate the corners of a cell
    const getCellBounds = (latitude, longitude, cellSize = 0.1) => [
        [latitude - cellSize / 2, longitude - cellSize / 2], // Southwest corner
        [latitude + cellSize / 2, longitude + cellSize / 2], // Northeast corner
    ];

    return (
        <MapContainer center={center} zoom={7} style={{ height: '500px', width: '100%' }}>
            {/* Add OpenStreetMap Tile Layer */}
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />

            {/* Render a rectangle for each data point */}
            {temperatureData.map((point, index) => {
                const bounds = getCellBounds(point.latitude, point.longitude, 0.1); // Ensure no overlap
                const color = getColor(point.tavg);

                return (
                    <Rectangle
                        key={index}
                        bounds={bounds}
                        pathOptions={{ fillColor: color, fillOpacity: 0.7, stroke: false }}
                    >
                        {/* Tooltip shows `tavg` on hover */}
                        <Tooltip>
                            <div>
                                <p><strong>Avg Temp:</strong> {point.tavg.toFixed(2)} Â°F</p>
                            </div>
                        </Tooltip>
                    </Rectangle>
                );
            })}
        </MapContainer>
    );
}

export default MapComponent;
