import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Register from './Register';
import Login from './Login';
import MapPage from './MapPage';

function App() {
  const [authenticated, setAuthenticated] = useState(false); // Track user authentication

  return (
    <Router>
      <div className="App">
        <Routes>
          {/* Root route: Show Login/Register page or redirect to /map if authenticated */}
          <Route
            path="/"
            element={
              authenticated ? (
                <Navigate to="/map" />
              ) : (
                <>
                  <h1>Please Login or Register</h1>
                  <Login setAuthenticated={setAuthenticated} />
                  <Register />
                </>
              )
            }
          />

          {/* Map route: Show MapPage only if authenticated, otherwise redirect to login */}
          <Route
            path="/map"
            element={
              authenticated ? (
                <MapPage setAuthenticated={setAuthenticated} />
              ) : (
                <Navigate to="/" />
              )
            }
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
