/**
 * DateSelector.js
 * 
 * Component for selecting the date, limited to the first day of each month.
 * Triggers data retrieval when "Enter" is pressed.
 */

import React, { useState } from 'react';

/**
 * DateSelector Component
 * 
 * Allows users to select a date, limited to the first day of each month,
 * and fetch temperature data on "Enter" key press.
 * 
 * @component
 * @param {function} fetchTemperatureData - Function to fetch data for the selected date.
 * @returns {JSX.Element} Date selector with restricted selection and enter key support.
 */
function DateSelector({ fetchTemperatureData }) {
    // Default date set to the first of the current month
    const [selectedDate, setSelectedDate] = useState(new Date().toISOString().substring(0, 7) + '-01');

    /**
     * handleDateChange
     * 
     * Updates the selected date and restricts it to the first day of each month.
     * 
     * @param {object} event - The date change event.
     */
    const handleDateChange = (event) => {
        const date = new Date(event.target.value);
        date.setDate(1); // Ensure the date is always set to the first of the month
        setSelectedDate(date.toISOString().substring(0, 10));
    };

    /**
     * handleKeyPress
     * 
     * Detects "Enter" key press to initiate data retrieval.
     * 
     * @param {object} event - The key press event.
     */
    const handleKeyPress = (event) => {
        if (event.key === 'Enter') {
            fetchTemperatureData(selectedDate);
        }
    };

    return (
        <div>
            <label>Select Date (First of Each Month Only): </label>
            <input
                type="date"
                value={selectedDate}
                onChange={handleDateChange}
                onKeyPress={handleKeyPress}
                max={new Date().toISOString().substring(0, 7) + '-01'}
            />
        </div>
    );
}

export default DateSelector;
