/**
 * MapComponent.js
 * 
 * React component that renders a heatmap for temperature data using Leaflet.
 * Each point represents a location with latitude, longitude, and temperature (tavg),
 * and displays this information in a popup when clicked.
 */

import React, { useEffect, useMemo } from 'react';
import 'leaflet/dist/leaflet.css';
import 'leaflet.heat';

/**
 * MapComponent
 * 
 * Renders a map with heatmap visualization for temperature data.
 * Each point includes a popup with details for latitude, longitude, and temperature.
 * 
 * @component
 * @param {Object[]} [temperatures=[]] - Array of objects with latitude, longitude, and tavg values.
 * @returns {JSX.Element} A map with heatmap and optional markers for additional data.
 */
function MapComponent({ temperatures = [] }) {
    const center = useMemo(() => [38.5976, -80.4549], []); // Center of WV

    useEffect(() => {
        if (temperatures.length === 0) return; // Skip rendering if no temperature data available

        const L = require('leaflet');
        require('leaflet.heat');

        // Initialize the map
        const map = L.map('heatmap').setView(center, 7);

        // Add OpenStreetMap tile layer
        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
            attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
        }).addTo(map);

        // Transform temperatures into heatmap points ([latitude, longitude, intensity])
        const heatmapData = temperatures.map((point) => [
            point.latitude,
            point.longitude,
            point.tavg,
        ]);

        // Add heatmap layer
        L.heatLayer(heatmapData, {
            radius: 25,
            blur: 20,
            maxZoom: 10,
            gradient: {
                0.0: '#002366',    // Very cold (dark blue)
                0.2: '#4169E1',    // Cold (royal blue)
                0.4: '#87CEEB',    // Cool (sky blue)
                0.6: '#FFD700',    // Warm (gold)
                0.8: '#FF4500',    // Hot (orange-red)
                1.0: '#B22222',    // Very hot (firebrick red)
            },
        }).addTo(map);

        // Cleanup on component unmount
        return () => {
            map.remove();
        };
    }, [temperatures, center]);

    return (
        <div id="heatmap" style={{ height: '500px', width: '100%' }}>
            {temperatures.length === 0 && (
                <p style={{ textAlign: 'center', marginTop: '20px' }}>
                    Please select a date to fetch and display the heatmap.
                </p>
            )}
        </div>
    );
}

export default MapComponent;
